document.addEventListener('DOMContentLoaded',()=>{mostrarDetallePeli()});function mostrarDetallePeli(){const parametrosURL=new URLSearchParams(window.location.search);const idPeli=(parametrosURL.get('id'));const url=`https://www.omdbapi.com/?apikey=45b5eb40&I=${idPeli}`;fetch(url).then(respuesta=>respuesta.json()).then(resultado=>{mostrarDetalle(resultado)})}
function mostrarDetalle(resultado){const{Poster,Plot,Title,Year,Director,Genre,Type,Country,Runtime}=resultado;const divContenido=document.createElement('div');const divPrincipal=document.querySelector('.contenedor');divContenido.innerHTML=`
        <img src="${Poster}" alt="" id="imagen" class="imagen-d">
        <div class="detalle-descripcion">
            <h1 class="titulo-detalle" id="titulo">${Title}</h1>
            <p class="descripcion-detalle" id="descripcion">${Plot}</p>
            <div class="extras-detalle">
                <div class="director-detalle ">
                    <span id="type">Tipo: ${Type}</span>
                    <span id="director">Director: ${Director}</span>
                    <span id="country">Pais: ${Country}</span>
                </div>
                <div class="lanzamiento-detalle">
                    <span id="year">Lanzamiento: ${Year}</span>
                    <span id="genre">Genero: ${Genre}</span>
                    <span id="runtime">Duracion: ${Runtime}</span>
                </div>
            </div>
            <a href="index.html" class="boton">Volver</a>
        </div>
        
    `;divContenido.classList.add('contenido-detalle','contenedor');divPrincipal.appendChild(divContenido)}